parameters.inputSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Log Entry Array Schema",
  "description": "Schema for validating an array of log entries with labels for n8n output formatter",
  "type": "array",
  "minItems": 1,
  "items": {
    "type": "object",
    "title": "Log Entry",
    "description": "A single log entry with message and classification label",
    "properties": {
      "log": {
        "type": "string",
        "title": "Log Message",
        "description": "The actual log line content",
        "minLength": 1,
        "maxLength": 10000
      },
      "label": {
        "type": "string",
        "title": "Classification Label",
        "description": "The classification category for the log entry",
        "enum": [
          "neutral",
          "suspect",
          "critical"
        ],
        "default": "neutral"
      }
    },
    "required": [
      "log",
      "label"
    ],
    "additionalProperties": false
  }
}
